    console.log(`delete ${id}`)
    try {
      const result = await axios.delete(`http://localhost:3001/clubs/${id}`)
      console.log(result)
      setClubData(result.data)
    } catch (err) {
      console.log(err)
    }
  }
  const handleEdit = () => {
    console.log(`edit ${id}`)
  }

  return (
    <>
      <div>
        <li>{club.clubName}</li>
        <li>{club.brand}</li>
        <button onClick={handleEdit}>Edit CLub</button>
        <button onClick={handleDelete}>Delete CLub</button>
      </div>
    </>
  )
}

