import React, {useState} from 'react'
import {v4 as uuidv4} from 'uuid'
import axios from 'axios'

const Clubs = ({clubData, setClubData}) => {
  const handleDelete = async (id) => {
    const newData = await axios.delete(`http://localhost:3001/clubs/${id}`)
    setClubData(newData.data)
  }
  const getAverageYards = (club) => {
    let totalYards = 0
    let shots = club.totalShots
    if (shots === 0) {
      return 0
    } else {
      club.yards.forEach((shot) => {
        totalYards += shot
      })
      return totalYards / shots
    }
  }
  const [shot, setShot] = useState()
  const setYards = (e) => {
    console.log(e.target.value)
    setShot(e.target.value)
  }

  const addShot = () => {}
  const renderInput = () => {
    return (
      <input type='text' placeholder='Yards' onChange={setYards} value={shot} />
    )
  }
  const input = renderInput()

  return clubData.map((club) => {
    const averageYards = getAverageYards(club)
    const key = uuidv4()
    const id = club._id
    return (
      <div key={key}>
        <div>{club.clubName}</div>
        <div>Shot Total {club.totalShots}</div>
        <button onClick={() => addShot(club)}>Add Shot</button>
        {/* {input} */}
        <div>Avg Yards {averageYards}</div>
        <button onClick={() => handleDelete(id)}>Delete</button>

        <form onSubmit={addShot}>
          <input type='text' placeholder='Club' onChange={() => setYards(e)} />
          <button onClick={addShot}>Add Club</button>
        </form>
      </div>
    )
  })
}

export default Clubs
