import React, {useState} from 'react'
import axios from 'axios'

const AddShotInput = ({club}) => {
  const [shot, setShot] = useState()
  const handleSubmit = (e) => {
    e.preventDefault()
    axios.patch(`http://localhost:3001/clubs/${club._id}`, {shot: shot})
    console.log('submit')
  }
  const handleChange = (e) => {
    setShot(e.target.value)
  }
  return (
    <form onSubmit={handleSubmit}>
      <input type='text' placeholder='Club' onChange={handleChange} />
      <button onClick={handleSubmit}>Add Shot</button>
      {/* <button onClick={() => addShot(club)}>Add Shot</button> */}
    </form>
  )
}

export default AddShotInput
