import React from 'react'
import {useState} from 'react'
import axios from 'axios'
import {Link} from 'react-router-dom'

const AddClub = () => {
  const [clubName, setClubName] = useState('')
  const [clubBrand, setClubBrand] = useState('')
  const [showInputMessage, setShowInputMessage] = useState(false)
  // Simple Check to see if input is empty
  const inputValid = (input) => {
    if (input !== '') {
      return true
    } else {
      return false
    }
  }

  const handleSubmit = async (e) => {
    e.preventDefault()
    if (inputValid(clubName) && inputValid(clubBrand)) {
      try {
        const result = await axios.post('http://localhost:3001/clubs', {
          clubName: clubName,
          brand: clubBrand,
          yards: [],
          totalShots: 0,
        })
        console.log(result)
        setClubName('')
        setClubBrand('')
      } catch (err) {
        console.log(err)
      }
    }
  }

  const handleNameChange = (e) => {
    setClubName(e.target.value)
  }
  const handleBrandChange = (e) => {
    setClubBrand(e.target.value)
  }

  return (
    <>
      <form>
        <input type='text' onChange={handleNameChange} />
        <input type='text' onChange={handleBrandChange} />

        {/* <Link to='/clubs'> */}
        <button onClick={handleSubmit}>Add CLub</button>
        {/* </Link> */}
        {showInputMessage && <div>Please enter all inputs</div>}
      </form>
    </>
  )
}

export default AddClub
