import React from 'react'
import {useState} from 'react'
import axios from 'axios'
import {Link} from 'react-router-dom'

const AddClub = () => {
  const [clubName, setClubName] = useState('')
  const [clubBrand, setClubBrand] = useState('')

  const clubNameValid = (clubName) => {
    if (clubName !== '') {
      return true
    } else {
      return false
    }
  }
  const clubBrandValid = (clubBrand) => {
    if (clubBrand !== '') {
      return true
    } else {
      return false
    }
  }

  const handleSubmit = async (e) => {
    e.preventDefault()
    if (!clubNameValid(clubName)) {
      console.log('club name not valid')
      return
    }
    if (!clubBrandValid(clubBrand)) {
      console.log('club brand not valid')
      return
    }
    try {
      const result = await axios.post('http://localhost:3001/clubs', {
        clubName: clubName,
        brand: clubBrand,
        yards: [],
        totalShots: 0,
      })
      console.log(result)
      setClubName('')
      setClubBrand('')
    } catch (err) {
      console.log(err)
    }
  }
  const handleNameChange = (e) => {
    setClubName(e.target.value)
  }
  const handleBrandChange = (e) => {
    setClubBrand(e.target.value)
  }

  return (
    <>
      <form>
        <input type='text' onChange={handleNameChange} />
        <input type='text' onChange={handleBrandChange} />

        {/* <Link to='/clubs'> */}
        <button onClick={handleSubmit}>Add CLub</button>
        {/* </Link> */}
      </form>
    </>
  )
}

export default AddClub
