import React, {useEffect, useState} from 'react'
import {useParams, useNavigate} from 'react-router-dom'
import axios from 'axios'
import {Link} from 'react-router-dom'

const Club = ({setClubData}) => {
  // const id = club._id
  const params = useParams()
  const id = params.id
  const [club, setClub] = useState()
  const [isLoading, setIsLoading] = useState(true)

  useEffect(() => {
    const fetchClub = async () => {
      const result = await axios.get(`http://localhost:3001/clubs/${id}`)
      console.log(result)
      setClub(result.data)
      setIsLoading(false)
    }
    fetchClub()
  }, [])

  const handleDelete = async () => {
    try {
      console.log('delete')
      const result = await axios.delete(`http://localhost:3001/clubs/${id}`)
      console.log(result)
      setClubData(result.data)
    } catch (err) {
      console.log(err)
    }
  }

  const handleEdit = () => {
    console.log(`edit ${id}`)
  }

  return (
    <>
      <div>Club Page</div>
      {isLoading ? (
        <div>Loading</div>
      ) : (
        <div style={{paddingTop: '10px'}}>
          <div>Club Id = {params.id}</div>
          <p>{club.clubName}</p>
          <p>{club.brand}</p>
          <Link to={`/clubs/edit/${id}`}>
            <button>Edit CLub</button>
          </Link>
          <button onClick={handleDelete}>Delete CLub</button>
        </div>
      )}
    </>
  )
}

export default Club
