import React, {useEffect, useState} from 'react'
import axios from 'axios'
import Container from 'react-bootstrap/Container'
import Stack from 'react-bootstrap/Stack'
import 'bootstrap/dist/css/bootstrap.min.css'
import ClubCard from './components/ClubCard'
import AddClubModel from './components/AddClubModel'
import Clubs from './components/Clubs'

const App = () => {
  const [clubData, setClubData] = useState([])
  const [isLoading, setIsLoading] = useState(true)

  const [show, setShow] = useState(false)

  const handleClose = () => setShow(false)
  const handleShow = () => setShow(true)

  useEffect(() => {
    const fetchData = async () => {
      try {
        const result = await axios.get(`http://localhost:3001/clubs`)
        console.log(result.data)
        setClubData(result.data)
        setIsLoading(false)
      } catch (err) {
        console.log(err)
      }
    }
    fetchData()
  }, [])
  return (
    <>
      <Container className='pt-3'>
        <Stack direction='horizontal' gap='2' className='mb-4'>
          <h1 className='me-auto'>Clubs</h1>
          <AddClubModel
            show={show}
            handleClose={handleClose}
            handleShow={handleShow}
            setClubData={setClubData}
          />
        </Stack>
        {/* <Form setClubData={setClubData} /> */}
        {isLoading && <div>Loading </div>}
        {!isLoading && <Clubs clubData={clubData} setClubData={setClubData} />}
        <ClubCard clubData={clubData} setClubData={setClubData} />
      </Container>
    </>
  )
}

export default App
