import React, {useState} from 'react'
import axios from 'axios'

const AddShotInput = ({club}) => {
  const [shot, setShot] = useState()
  const handleSubmit = (id) => {
    e.preventDefault()
    console.log(id)
    axios.patch(`http://localhost:3001/clubs/${id}`, {shot: shot})
    console.log('submit')
  }
  const handleChange = (e) => {
    setShot(e.target.value)
  }
  return (
    <>
      <input type='text' placeholder='Club' onChange={handleChange} />
      <button onClick={() => handleSubmit(club._id)}>Add Shot</button>
      {/* <button onClick={() => addShot(club)}>Add Shot</button> */}
    </>
  )
}

export default AddShotInput
