import {Button, Card, ProgressBar, Stack} from 'react-bootstrap'
import axios from 'axios'
import {v4 as uuidv4} from 'uuid'

export default function BudgetCard({clubData, setClubData}) {
  const handleDelete = async (id) => {
    const newData = await axios.delete(`http://localhost:3001/clubs/${id}`)
    setClubData(newData.data)
  }
  const getAverageYards = (club) => {
    let totalYards = 0
    let shots = club.totalShots
    if (shots === 0) {
      return 0
    } else {
      club.yards.forEach((shot) => {
        totalYards += shot
      })
      return totalYards / shots
    }
  }
  return clubData.map((club) => {
    const averageYards = getAverageYards(club)
    const key = uuidv4()
    const id = club._id
    return (
      <Card key={key}>
        <Card.Body>
          <div className='fs-3 d-flex justify-content-between align-items-baseline fw-normal mb-3'>
            <div className='me-2'>{club.clubName}</div>
            <div className='text-muted'>club brand</div>
          </div>
          <div className='d-flex justify-content-between pb-2'>
            <div className='d-flex flex-column'>
              <Card.Text className='m-0'>
                Average Shot : {averageYards} Yards
              </Card.Text>
              <Card.Text className='pb-2'>Longest Shot: 200 Yards</Card.Text>
            </div>

            <Card.Text>Total Shots: {club.totalShots}</Card.Text>
          </div>
          <div className='m-1 fs-5'>Goal 100/200 Yards</div>
          <ProgressBar
            className='rounded-pill'
            // variant={getProgressBarVariant(amount, max)}
            min={0}
            max={1000}
            now={500}
          />
          <Stack direction='horizontal' gap='2' className='mt-4'>
            <Button
              variant='outline-primary'
              className='ms-auto'
              onClick={() => console.log('add shot')}>
              Add Shot
            </Button>
            <Button
              onClick={() => console.log('View shots')}
              variant='outline-secondary'>
              View Shots
            </Button>
            <Button onClick={() => handleDelete(id)} variant='outline-danger'>
              Delete Club
            </Button>
          </Stack>
        </Card.Body>
      </Card>
    )
  })
}
