import React from 'react'
import {useState} from 'react'
import {Modal, Form, Button} from 'react-bootstrap'
import axios from 'axios'

const AddShotModal = ({
  addShotHandleClose,
  addShotHandleShow,
  showAddShotModal,
  setClubData,
  club,
}) => {
  const [shot, setShot] = useState()
  const id = club._id
  const [warning, setWarning] = useState(false)

  const isWarningMessage = () => {
    if (warning) {
      return <div>Warning</div>
    } else {
      console.log('not warning')
    }
  }
  const isNumeric = (str) => {
    return str != null && str.matches('[0-9.]+')
  }

  const handleSubmit = async (id) => {
    console.log(isNumeric(shot))
    if (isNumeric(shot) === true) {
      const result = await axios.patch(`http://localhost:3001/clubs/${id}`, {
        shot: parseInt(shot),
        club: club,
      })
      setClubData(result.data)
      addShotHandleClose()
    } else {
      console.log('stop submit')
      setWarning(true)
    }
  }
  const handleChange = (e) => {
    setShot(e.target.value)
  }

  return (
    <>
      <Button variant='primary' onClick={addShotHandleShow}>
        Add Shot
      </Button>

      <Modal show={showAddShotModal} onHide={addShotHandleClose}>
        <Modal.Header closeButton>
          <Modal.Title>Add Shot</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form>
            <Form.Group className='mb-3' controlId='exampleForm.ControlInput1'>
              <Form.Label>Total Yards</Form.Label>
              <Form.Control
                className='mb-2'
                type='text'
                placeholder='Yards'
                autoFocus
                onChange={handleChange}
              />
            </Form.Group>
          </Form>
        </Modal.Body>
        <Modal.Footer>
          <Button variant='secondary' onClick={addShotHandleClose}>
            Cancel
          </Button>
          <Button variant='primary' onClick={() => handleSubmit(id)}>
            Add
          </Button>
        </Modal.Footer>
        {warning && isWarningMessage}
      </Modal>
    </>
  )
}

export default AddShotModal
