import {Button, Card, ProgressBar, Stack} from 'react-bootstrap'
import axios from 'axios'

export default function BudgetCard({clubData, setClubData}) {
  const handleDelete = async (id) => {
    const newData = await axios.delete(`http://localhost:3001/clubs/${id}`)
    setClubData(newData.data)
  }
  const getAverageYards = (club) => {
    let totalYards = 0
    let shots = club.totalShots
    if (shots === 0) {
      return 0
    } else {
      club.yards.forEach((shot) => {
        totalYards += shot
      })
      return totalYards / shots
    }
  }

  //   return clubData.map((club) => {
  //     const averageYards = getAverageYards(club)
  //     const key = uuidv4()
  //     const id = club._id
  //     return (
  //       <div key={key}>
  //         <div>{club.clubName}</div>
  //         <div>Shot Total {club.totalShots}</div>
  //         <div>Avg Yards {averageYards}</div>
  //         <button onClick={() => handleDelete(id)}>Delete</button>
  //         <AddShotInput club={club} setClubData={setClubData} />
  //         <Button variant='primary' onClick={() => console.log('hello')}>
  //           Primary
  //         </Button>
  //       </div>
  //     )
  //   })
  // }

  return clubData.map((club) => {
    console.log(club)
    const averageYards = getAverageYards(club)
    const key = uuidv4()
    const id = club._id
    return (
      <Card key={key}>
        <Card.Body>
          <Card.Title className='d-flex justify-content-between align-items-baseline fw-normal mb-3'>
            <div className='me-2'>{club.clubName}</div>
            <div className='text-muted'>club brand</div>
          </Card.Title>
          <div className='d-flex justify-content-between'>
            <div className='d-flex flex-column'>
              <Card.Text className='m-0'>Avg Distance: 150 Yards</Card.Text>
              <Card.Text className='pb-2'>Longest Shot: 200 Yards</Card.Text>
            </div>

            <Card.Text>Total Shots: 10 {club.totalShots}</Card.Text>
          </div>

          <ProgressBar
            className='rounded-pill'
            // variant={getProgressBarVariant(amount, max)}
            min={0}
            max={1000}
            now={500}
          />
          <Stack direction='horizontal' gap='2' className='mt-4'>
            <Button
              variant='outline-primary'
              className='ms-auto'
              onClick={() => console.log('add shot')}>
              Add Shot
            </Button>
            <Button
              onClick={() => console.log('View shots')}
              variant='outline-secondary'>
              View Shots
            </Button>
            <Button onClick={handleDelete} variant='outline-danger'>
              Delete Club
            </Button>
          </Stack>
        </Card.Body>
      </Card>
    )
  })
}
