import {useRef, useState, useEffect} from 'react'
import axios from 'axios'
import {Link} from 'react-router-dom'
import {FontAwesomeIcon} from '@fortawesome/react-fontawesome'
import {faCheck} from '@fortawesome/free-solid-svg-icons'

const USER_REGEX = /^.{3,}$/
const PWD_REGEX = /^.{5,}$/
const REGISTER_URL = '/register'

const Register = () => {
  const userRef = useRef()
  const errRef = useRef()

  const [user, setUser] = useState('')
  const [validName, setValidName] = useState(false)
  const [userFocus, setUserFocus] = useState(false)

  const [pwd, setPwd] = useState('')
  const [validPwd, setValidPwd] = useState(false)
  const [pwdFocus, setPwdFocus] = useState(false)

  const [matchPwd, setMatchPwd] = useState('')
  const [validMatch, setValidMatch] = useState(false)
  const [matchFocus, setMatchFocus] = useState(false)

  const [errMsg, setErrMsg] = useState('')
  const [success, setSuccess] = useState(false)

  // Set focus on the username input
  // useEffect(() => {
  //   userRef.current.focus()
  // }, [])

  // Set validName state when user input changes
  useEffect(() => {
    setValidName(USER_REGEX.test(user))
  }, [user])

  // Set validPwd and ValidMatch state when the two password inputs change
  useEffect(() => {
    setValidPwd(PWD_REGEX.test(pwd))
    setValidMatch(pwd === matchPwd)
  }, [pwd, matchPwd])

  // Clear the error message anytime the state changes
  useEffect(() => {
    setErrMsg('')
  }, [user, pwd, matchPwd])

  const handleSubmit = async (e) => {
    e.preventDefault()
    // if button enabled with JS hack
    const v1 = USER_REGEX.test(user)
    const v2 = PWD_REGEX.test(pwd)
    if (!v1 || !v2) {
      setErrMsg('Invalid Entry')
      return
    }
    try {
      const response = await axios.post(
        REGISTER_URL,
        JSON.stringify({user, pwd}),
        {
          headers: {'Content-Type': 'application/json'},
          withCredentials: true,
        }
      )
      console.log(response?.data)
      console.log(response?.accessToken)
      console.log(JSON.stringify(response))
      setSuccess(true)
      //clear state and controlled inputs
      //need value attrib on inputs for this
      setUser('')
      setPwd('')
      setMatchPwd('')
    } catch (err) {
      if (!err?.response) {
        setErrMsg('No Server Response')
      } else if (err.response?.status === 409) {
        setErrMsg('Username Taken')
      } else {
        setErrMsg('Registration Failed')
      }
    }
  }

  return (
    <>
      {success ? (
        <section>
          <h1>Success!</h1>
          <p>
            <Link to='/login'>
              <button className=''>Login</button>
            </Link>
          </p>
        </section>
      ) : (
        // <section>
        //   {errMsg && <p ref={errRef}> {errMsg}</p>}
        //   <h1 className='text-2xl pb-5'>Register</h1>
        //   <form onSubmit={handleSubmit}>
        //     <div className='p-2'>
        //       <label htmlFor='username'>Username:</label>
        //       <input
        //         type='text'
        //         className='border-2'
        //         autoComplete='off'
        //         id='username'
        //         onChange={(e) => setUser(e.target.value)}
        //         onFocus={() => setUserFocus(true)}
        //         onBlur={() => setUserFocus(false)}
        //         value={user}
        //         required
        //       />
        //       {validName && <FontAwesomeIcon icon={faCheck} color='green' />}
        //
        //     </div>
        //     <div className='p-2'>
        //       <label htmlFor='password'>Password:</label>
        //       <input
        //         type='password'
        //         className='border-2'
        //         id='password'
        //         onChange={(e) => setPwd(e.target.value)}
        //         value={pwd}
        //         required
        //       />
        //     </div>
        //     <div className='p-2'>
        //       <label htmlFor='confirm_pwd'>Confirm Password:</label>
        //       <input
        //         type='password'
        //         id='confirm_pwd'
        //         className='border-2'
        //         onChange={(e) => setMatchPwd(e.target.value)}
        //         value={matchPwd}
        //         required
        //       />
        //       <p>8-24 characters long</p>
        //       <p>include upper/lowercase letter and one didgit</p>
        //     </div>
        //     <div className='p-2'>{errMsg}</div>
        //     <div className='flex p-2'>
        //       <button className='bg-pink-300 mr-2'>Sign Up</button>
        //       <p>
        //         <Link to='/login'>
        //           <button className='bg-pink-300'>Login</button>
        //         </Link>
        //       </p>
        //     </div>
        //   </form>
        // </section>
        <div className='bg-grey-lighter min-h-screen flex flex-col'>
          <div className='container max-w-sm mx-auto flex-1 flex flex-col items-center justify-center px-2'>
            <div className='bg-white px-6 py-8 rounded shadow-md text-black w-full'>
              <h1 className='mb-8 text-3xl text-center'>Sign up</h1>
              <label htmlFor='username'>Username:</label>
              <input
                type='text'
                name='userName'
                placeholder='User Name'
                className='block border border-grey-light w-full p-3 rounded mb-0'
                onChange={(e) => setUser(e.target.value)}
                onFocus={() => setUserFocus(true)}
                onBlur={() => setUserFocus(false)}
                value={user}
              />
              <p className={!validName ? 'block text-xs' : 'hidden'}>
                Use 4 or more characters
              </p>

              <label htmlFor='password'>Password:</label>
              <input
                type='password'
                name='password'
                placeholder='Password'
                className='block border border-grey-light w-full p-3 rounded'
                onChange={(e) => setPwd(e.target.value)}
                value={pwd}
              />
              <p className={!validPwd ? 'block text-xs' : 'hidden'}>
                Use 5 or more characters <br />
              </p>
              <label htmlFor='confirm-password'>Password:</label>
              <input
                type='password'
                name='confirm-password'
                placeholder='Confirm Password'
                className='block border border-grey-light w-full p-3 rounded'
                onChange={(e) => setMatchPwd(e.target.value)}
                value={matchPwd}
              />
              <p className={!validMatch ? 'block text-xs' : 'hidden'}>
                Passwords must match <br />
              </p>

              <button
                type='submit'
                className='w-full text-center py-3 rounded bg-green-600 text-white hover:bg-green-500 focus:outline-none my-1'>
                Create Account
              </button>
            </div>
            <div className='text-grey-dark mt-6 text-center'>
              Already have an account?
              <Link to='/login' className='pl-3'>
                <button className='w-full text-center py-2 rounded border-2 border-green-600 bg-white text-green hover:white focus:outline-none my-1'>
                  Login
                </button>
              </Link>
              .
            </div>
          </div>
        </div>
      )}
    </>
  )
}

export default Register
