import React from 'react'
import {v4 as uuidv4} from 'uuid'
import axios from 'axios'

const Clubs = ({clubData, setClubData}) => {
  const handleDelete = async (id) => {
    const newData = await axios.delete(`http://localhost:3001/clubs/${id}`)
    setClubData(newData.data)
  }
  const getAverageYards = () => {
    let totalYards = 0
    let shots = clubData.shots
    clubData.forEach((item) => {
      totalYards += item.yards
    })
    return totalYards / shots
  }

  return clubData.map((item) => {
    const averageYards = getAverageYards()
    console.log(averageYards)
    const key = uuidv4()
    const id = item._id
    return (
      <div key={key}>
        <div>
          <div>{item.club}</div>
          <div>Shot Total {item.shots}</div>
          <div>Avg Yards {averageYards === NaN && averageYards}</div>
          <button onClick={() => handleDelete(id)}>Delete</button>
        </div>
      </div>
    )
  })
}

export default Clubs
